mapper.insert:
insert into fund (
fund_no, post_no, fund_price, fund_remain, fund_time, member_id, fund_title
) 
values (
#{fundNo}, #{postNo}, #{fundPrice}, #{fundRemain}, sysdate, #{memberId}, #{fundTitle}
)

mapper.select:
select * from fund 
where member_id = #{memberId}
order by fund_no desc

		select sum(fund_price) as fund_total
           from fund 
           where post_no=#{postNo}

mapper.update:

UPDATE fund
SET fund_remain = 0
WHERE fund_no = #{fundNo}

UPDATE fund
SET fund_total = fund_total - #{fundPrice}
WHERE fund_no = #{fundNo}
