mapper.insert:
insert into fund_post(
			post_no, member_id, fund_title, post_start, post_end,
			fund_goal, fund_sponsor_count, fund_state
			)
values(
#{postNo}, #{memberId}, #{fundTitle}, #{postStart}, 
#{postEnd}, #{fundGoal}, #{fundSponsorCount}, #{fundState}
)

mapper.update:
update fund_post
set fund_state=#{fundState}
where post_no=#{postNo}
