mapper.select:
select * from report 
where 
    member_id = #{memberId} 
    and 
    report_target_type = #{reportTargetType}
    and
    report_primary_key = #{reportPrimaryKey}

mapper.update:
update report set report_for = #{reportFor} 
where 
    member_id = #{memberId} 
    and 
    report_target_type = #{reportTargetType}
    and
    report_target_primary_key = #{reportTargetPrimaryKey}
